def floor_calculator(input):
    count = 0
    inter_count = 1
    first_basement = False
    for i in input:
        if i == "(":
            count += 1
        elif i ==")":
            count -= 1

        if not first_basement:
            if count == -1:
                # print(inter_count)
                first_basement = True
            else:
                inter_count += 1
    return count, inter_count

